				TableSchema tablaRelacionadaObjetivo;							
					
				foreach(CollectionInfo collectionInfo in hashChildrenTablesFinal.Values)	{
					
					if(collectionInfo.CollectionRelationshipType==RelationshipType.OneToOne) {
